增加一个文件的最大大小，通过将inode的一个直接块变成一个二次间接块 fs.c balloc在bitmap中查找unused block，使用bio.c中的bread和brelse来同步访问 when to use log-write？ After finishing all writes on the buf. fs.c/bmap 的作用 在读取inode节点数据的时候(readi, writei)，将地址转换为块所在地址。 将原来的直接块中分一个出来作为二次间接块的一级块 // fs.h
#define NDIRECT 11
#define NINDIRECT (BSIZE / sizeof(uint))
#define NDINDIRECT (NINDIRECT * NINDIRECT) // Double-indirect block
#define MAXFILE (NDIRECT + NINDIRECT + NDINDIRECT) // On-disk inode structure
struct dinode { short type; // File type short major; // Major device number (T_DEVICE only) short minor; // Minor device number (T_DEVICE only) short nlink; // Number of links to inode in file system uint size; // Size of file (bytes) uint addrs[NDIRECT+2]; // Data block addresses
}; // file.h
// 保证On-dist inode和 内存inode的 addrs 大小一致
// in-memory copy of an inode
struct inode { uint dev; // Device number uint inum; // Inode number int ref; // Reference count struct sleeplock lock; // protects everything below here int valid; // inode has been read from disk? short type; // copy of disk inode short major; short minor; short nlink; uint size; uint addrs[NDIRECT+2];
}; 在fs.c中具体实现二次间接块 static uint
bmap(struct inode *ip, uint bn) { uint addr, *a; struct buf *bp; if (bn < NDIRECT) { if ((addr = ip->addrs[bn]) == 0) ip->addrs[bn] = addr = balloc(ip->dev); return addr; } bn -= NDIRECT; if (bn < NINDIRECT) { // Load indirect block, allocating if necessary. if ((addr = ip->addrs[NDIRECT]) == 0) ip->addrs[NDIRECT] = addr = balloc(ip->dev); bp = bread(ip->dev, addr); a = (uint *) bp->data; if ((addr = a[bn]) == 0) { a[bn] = addr = balloc(ip->dev); log_write(bp); } brelse(bp); return addr; } bn -= NINDIRECT; if (bn < NDINDIRECT) { if ((addr = ip->addrs[NDIRECT + 1]) == 0) { // 暂未分配一级间接块 ip->addrs[NDIRECT+1] = addr = balloc(ip->dev); } bp = bread(ip->dev, addr); // 一级间接块 a = (uint *) bp->data; int first_index = bn / NINDIRECT; if ((addr = a[first_index]) == 0) { // 暂未分配二级间接块 a[first_index] = addr = balloc(ip->dev); log_write(bp); } brelse(bp); bp = bread(ip->dev, addr); // 二级间接块内容 a = (uint *)bp->data; int second_index = bn % NINDIRECT; if ((addr = a[second_index]) == 0) { // 暂未分配数据块 a[second_index] = addr = balloc(ip->dev); log_write(bp); } brelse(bp); return addr; } panic("bmap: out of range");
} void
itrunc(struct inode *ip)
{ int i, j, k; struct buf *bp, *bp2; uint *a, *a2; for(i = 0; i < NDIRECT; i++){ if(ip->addrs[i]){ bfree(ip->dev, ip->addrs[i]); ip->addrs[i] = 0; } } if(ip->addrs[NDIRECT]){ bp = bread(ip->dev, ip->addrs[NDIRECT]); a = (uint*)bp->data; for(j = 0; j < NINDIRECT; j++){ if(a[j]) bfree(ip->dev, a[j]); } brelse(bp); bfree(ip->dev, ip->addrs[NDIRECT]); ip->addrs[NDIRECT] = 0; } if(ip->addrs[NDIRECT+1]) { bp = bread(ip->dev, ip->addrs[NDIRECT+1]); // 一级间接块 a = (uint *)bp->data; for (j = 0; j < NINDIRECT; j++) { if (a[j]) { bp2 = bread(ip->dev, a[j]); a2 = (uint*)bp2->data; for (k = 0; k < NINDIRECT; k++) { if (a2[k]) { bfree(ip->dev, a2[k]); } } brelse(bp2); bfree(ip->dev, a[j]); } } brelse(bp); bfree(ip->dev, ip->addrs[NDIRECT+1]); ip->addrs[NDIRECT+1] = 0; } ip->size = 0; iupdate(ip);
} 运行测试 $bigfile........................................................................................................
