使用管道实现素数筛，每一个阶段实现打印管道中到来的第一个素数a，判断剩余到达的数是否能被a整除，如果不能，则送入下一阶段的管道。 参考 实现。 #include "kernel/types.h"
#include "kernel/stat.h"
#include "user/user.h" const int limit = 35; void panic(char* buf) { fprintf(2, "%s\n", buf); exit(1);
} void cull(int readPipe, int writePipe, int prime) { uint32 n; while (read(readPipe, &n, 4)) { if (n % prime != 0) { write(writePipe, &n, 4); } }
}
/** * return the another pipe contains group of numbers that cannot be divided by prime * @param prime * @param readPipe * @return */
void primeFilters(int readPipe) { uint32 prime; if (read(readPipe, &prime, 4)) { printf("prime %d\n", prime); int newPipe[2]; if (pipe(newPipe) < 0) { panic("cannot create pipe."); } if (fork() == 0) { // child generate new sequence close(newPipe[0]); cull(readPipe, newPipe[1], prime); close(newPipe[1]); } else { // parent forward the pipe to next stage close(newPipe[1]); primeFilters(newPipe[0]); close(newPipe[0]); } }
} int main(void) { int p[2]; if (pipe(p) < 0) { panic("Cannot create pipe."); } if (fork() == 0) { // child close(p[1]); primeFilters(p[0]); close(p[0]); } else { // parent close(p[0]); for (uint32 i = 2; i <= limit; ++i) { write(p[1], &i, 4); } close(p[1]); wait(0); // wait all children } exit(0);
}

