{ int listenfd, connfd; char hostname[MAXLINE], port[MAXLINE]; socklen_t clientlen; struct sockaddr_storage clientaddr; if (argc != 2) { fprintf(stderr, "usage: %s 
", argv[0]); exit(1); } listenfd = Open_listenfd(argv[1]); while (1) { clientlen = sizeof(clientaddr); connfd = Accept(listenfd, (SA *)&clientaddr, &clientlen); Getnameinfo((SA *) &clientaddr, clientlen, hostname, MAXLINE, port, MAXLINE, 0); printf("Accepted connection from (%s, %s)
", hostname, port); proxying(connfd); Close(connfd); }
} proxying函数是整个代理流程的抽象，读取客户端的请求，创建与服务器的连接socket（proxyfd），转发response。 void proxying(int fd) { char buf[MAXLINE], method[MAXLINE], uri[MAXLINE], version[MAXLINE]; char hostname[MAXLINE], port[MAXLINE], query[MAXLINE]; rio_t rio; Rio_readinitb(&rio, fd); if (!Rio_readlineb(&rio, buf, MAXLINE)) // read request line return; printf("Request line:%s", buf); char host[MAXLINE]; // request header host read_requesthdrs(&rio, host); // read request header sscanf(buf, "%s %s %s", method, uri, version); parseuri(uri, hostname, port, query); if (strlen(host) == 0) { strcpy(host, hostname); } // open connection to server int proxyfd = Open_clientfd(hostname, port); // send request to server send_requesthdrs(proxyfd, method, query, host); // Read response from server forward_response(proxyfd, fd); Close(proxyfd);
} 其他帮助函数 int parseuri(char* uri, char* hostname, char* port, char* query) { char *result = uri; char delim = '/'; result = index(uri, delim); result += 2; // skip '//' char *start = result; result = index(result, delim); if (!result) { // 不规范的uri，如 http://localhost:8080 strcpy(hostname, uri); strcpy(query, &delim); } else { strncpy(hostname, start, result - start); hostname[result - start] = '\0'; strcpy(query, result); } start = index(hostname, ':'); if (start) { hostname[start - hostname] = '\0'; start++; // skip ':' strcpy(port, start); } else { strcpy(port, "80"); } Dprintf("hostname=%s, port=%s, query=%s
", hostname, port, query); return 1;
} void read_requesthdrs(rio_t *rp, char* host)
{ char buf[MAXLINE]; Rio_readlineb(rp, buf, MAXLINE); printf("%s", buf); while(strcmp(buf, "
")) { //line:netp:readhdrs:checkterm Rio_readlineb(rp, buf, MAXLINE); printf("%s", buf); char* p = index(buf, ':'); if (strncmp("Host", buf, p-buf) == 0) { strcpy(host, p+1); } } return;
} void send_requesthdrs(int clientfd, char* method, char* query, char* host) { char buf[MAXLINE]; sprintf(buf, "%s %s HTTP/1.0
", method, query); // send request to server sprintf(buf, "%sHost: %s
", buf, host); strcat(buf, user_agent_hdr); strcat(buf, "Connection: close
"); strcat(buf, "Proxy-Connection: close

"); Rio_writen(clientfd, buf, strlen(buf)); Dprintf("%s", buf);
} // read from proxyfd and forward to clientfd
void forward_response(int proxyfd, int clientfd) { rio_t rio; int n; char buf[MAXLINE]; char response_header[MAXLINE]; int flag = 1; Rio_readinitb(&rio, proxyfd); while ((n = Rio_readlineb(&rio, buf, MAXLINE)) != 0) { Rio_writen(clientfd, buf, n); // for debug print if (flag && strcmp(buf, "
") == 0) { Fputs("Response header:
