// Role is the server's role
type Role int const ( FOLLOWER = 0 CANDIDATE = 1 LEADER = 2
) type LogEntry struct { Command interface{} Term int // 这条logEntry是在term时期被写入的
} //
// A Go object implementing a single Raft peer.
//
type Raft struct { mu sync.Mutex // Lock to protect shared access to this peer's state peers []*labrpc.ClientEnd // RPC end points of all peers persister *Persister // Object to hold this peer's persisted state me int // this peer's index into peers[] dead int32 // set by Kill() // Your data here (2A, 2B, 2C). // Look at the paper's Figure 2 for a description of what // state a Raft server must maintain. currentTerm int votedFor int // vote for the candidate id log []LogEntry // log entries role Role commitIndex int // index of log entry of the highest lastApplied int nextIndex []int // 对于每个server，需要发送的日志索引 matchIndex []int // 对每个server，已知的已经被冗余了的日志最高索引 // channel for communication chAppendEntry chan struct{} // 用于通信的管道， receive the heartbeat chRequestVote chan struct{} // channel, receive the request vote
} // return currentTerm and whether this server
// believes it is the leader.
func (rf *Raft) GetState() (int, bool) { var term int var isleader bool // Your code here (2A). rf.mu.Lock() term = rf.currentTerm isleader = (rf.role == LEADER) rf.mu.Unlock() return term, isleader
} GetState函数 // return currentTerm and whether this server
// believes it is the leader.
func (rf *Raft) GetState() (int, bool) { var term int var isleader bool // Your code here (2A). rf.mu.Lock() term = rf.currentTerm isleader = (rf.role == LEADER) rf.mu.Unlock() return term, isleader
} Make函数启动一个Raft节点 func Make(peers []*labrpc.ClientEnd, me int, persister *Persister, applyCh chan ApplyMsg) *Raft { rf := &Raft{} rf.peers = peers rf.persister = persister rf.me = me // Your initialization code here (2A, 2B, 2C). rf.currentTerm = 0 rf.votedFor = -1 rf.log = make([]LogEntry, 16) rf.log[0] = LogEntry{struct{}{}, -1} rf.commitIndex = 0 rf.lastApplied = 0 rf.matchIndex = make([]int, len(rf.peers)) rf.nextIndex = make([]int, len(rf.peers)) rf.chAppendEntry = make(chan struct{}) rf.chRequestVote = make(chan struct{}) rf.role = FOLLOWER // 开启服务 go rf.startServing() // initialize from state persisted before a crash rf.readPersist(persister.ReadRaftState()) return rf
} 主要逻辑函数startServing func (rf *Raft) startServing() { for { if rf.killed() { return } switch rf.role { case FOLLOWER: rf.following() case CANDIDATE: rf.election() case LEADER: rf.sendingHeartBeat() } }
} func (rf *Raft) changeRole(role Role) { rf.mu.Lock() defer rf.mu.Unlock() rf.role = role
} 作为Follower的处理逻辑 func (rf *Raft) following() { rand.Seed(time.Now().Unix()) electionTimeout := rand.Intn(1000) + 400 // in the thesis use 150~300ms, but here use 500~1000ms timer := time.NewTimer(time.Millisecond * time.Duration(electionTimeout)) for { if rf.killed() { return } select { case <-timer.C: DPrintf("%d: Eletion Timeout, start election\n", rf.me) rf.changeRole(CANDIDATE) return case <-rf.chAppendEntry: electionTimeout := rand.Intn(1000) + 400 timer.Reset(time.Millisecond * time.Duration(electionTimeout)) case <-rf.chRequestVote: DPrintf("%d: recieve the candidates' request for vote\n", rf.me) electionTimeout := rand.Intn(1000) + 400 timer.Reset(time.Millisecond * time.Duration(electionTimeout)) } }
} 作为Candidate的处理逻辑 func (rf *Raft) election() { rf.mu.Lock() rf.currentTerm++ rf.votedFor = rf.me rf.mu.Unlock() args := RequestVoteArgs{} args.CandidateID = rf.me args.Term = rf.currentTerm args.LastLogIndex = len(rf.log) - 1 args.LastLogTerm = rf.log[args.LastLogIndex].Term voteCount := 0 voteOk := make(chan struct{}) var voteMu sync.Mutex var notified bool = false // 是否已经发送给voteOk信号 timer := time.NewTimer(time.Millisecond * time.Duration(rand.Intn(300)+500)) for serverID := range(rf.peers) { if serverID != rf.me { go func(server int) { reply := RequestVoteReply{} if ok := rf.sendRequestVote(server, &args, &reply); ok { if reply.VoteGranted { voteMu.Lock() voteCount++ if !notified && voteCount >= len(rf.peers) / 2 { voteOk <- struct{}{} notified = true // 其他的选票就不再通知了 voteMu.Unlock() } else { voteMu.Unlock() } } } }(serverID) } } select { case <-voteOk: DPrintf("%d: Become Leader", rf.me) rf.changeRole(LEADER) rf.votedFor = -1 return case <-rf.chAppendEntry: // 当有其他的leader已经被选举出来后 rf.changeRole(FOLLOWER) rf.votedFor = -1 return case <-rf.chRequestVote: // 收到其他candidate的合法选票 rf.changeRole(FOLLOWER) rf.votedFor = -1 DPrintf("Receive others' requestVote") return case <-timer.C: // 到时间了还没有选票 rf.changeRole(FOLLOWER) rf.votedFor = -1 return }
} 作为Master的处理逻辑 func (rf *Raft) sendingHeartBeat() { rf.mu.Lock() lastLogIndex := len(rf.log) - 1 for i := range(rf.peers) { rf.nextIndex[i] = lastLogIndex + 1 rf.matchIndex[i] = 0 } args := AppendEntriesArgs{} args.Term = rf.currentTerm args.LeaderID = rf.me args.PrevLogIndex = lastLogIndex // index of log entry immediately preceding new ones args.PrevLogTerm = rf.log[args.PrevLogIndex].Term args.Entries = []LogEntry{} // empty log entries args.LeaderCommit = rf.commitIndex rf.mu.Unlock() timer := time.NewTimer(time.Duration(time.Millisecond * 200)) // 间隔200ms发出心跳 higherTermCh := make(chan struct{}) gotHigherTerm := false // 是否收到了高Term的回复 for { for serverID := range(rf.peers) { if serverID == rf.me { continue } go func(server int) { reply := AppendEntriesReply{} if ok := rf.sendAppendEntry(server, &args, &reply); ok { rf.mu.Lock() if !gotHigherTerm && reply.Term > rf.currentTerm { rf.currentTerm = reply.Term gotHigherTerm = true higherTermCh <- struct{}{} } rf.mu.Unlock() } }(serverID) } select { case <-timer.C: timer.Reset(time.Duration(time.Millisecond * 200)) case <-higherTermCh: // discover a server with higher term rf.changeRole(FOLLOWER) return case <-rf.chAppendEntry: // 如果接收到follower的高term AppendEntry rf.changeRole(FOLLOWER) return case <-rf.chRequestVote: // 如果接收到candidate的高term RequestVote rf.changeRole(FOLLOWER) return } }
} AppendEntries（心跳接受的RPC） func (rf *Raft) AppendEntry(args *AppendEntriesArgs, reply *AppendEntriesReply) { // follower 接收心跳处理 rf.mu.Lock() defer rf.mu.Unlock() if len(args.Entries) == 0 { if args.Term < rf.currentTerm { // 收到的心跳Term太小 reply.Success = false reply.Term = rf.currentTerm } else { DPrintf("%d: Got valid heartbeat from %d\n", rf.me, args.LeaderID) rf.currentTerm = args.Term reply.Success = true go func(){ rf.chAppendEntry <- struct{}{} }() } }
} RequestVote（投票请求的处理） type RequestVoteArgs struct { // Your data here (2A, 2B). Term int // candidate's term CandidateID int // 暂时用rafts数组的下标作为id LastLogIndex int // 最后一个日志条目的下标 LastLogTerm int
} //
// example RequestVote RPC reply structure.
// field names must start with capital letters!
//
type RequestVoteReply struct { // Your data here (2A). Term int // currentTerm, for candidate to update itself VoteGranted bool // true表示同意选举
} func (rf *Raft) RequestVote(args *RequestVoteArgs, reply *RequestVoteReply) { // Your code here (2A, 2B). rf.mu.Lock() defer rf.mu.Unlock() if args.Term < rf.currentTerm { reply.VoteGranted = false reply.Term = rf.currentTerm DPrintf("%d: refuse vote to %d\n", rf.me, args.CandidateID) } else if rf.votedFor == -1 || rf.votedFor == args.CandidateID { curLogIndex := len(rf.log) - 1 if args.LastLogIndex >= curLogIndex && args.LastLogTerm >= rf.log[curLogIndex].Term { // candidates的log至少和我一样新 DPrintf("%d: Vote to %d\n", rf.me, args.CandidateID) reply.VoteGranted = true reply.Term = args.Term rf.currentTerm = args.Term rf.votedFor = args.CandidateID go func() { rf.chRequestVote <- struct{}{} }() // 通知自己收到了合法的请求投票 } else { reply.VoteGranted = false // reply.Term = rf.currentTerm DPrintf("%d: refuese vote to %d, because the old log\n", rf.me, args.CandidateID) } } else { reply.VoteGranted = false // reply.Term = rf.currentTerm DPrintf("%d: refuese vote to %d, because has voted->%d.\n", rf.me, args.CandidateID, rf.votedFor) }
} 运行结果 ➜ raft git:(master) ✗ go test -run 2A -race
