实现过程中遇到很多Segmentation fault，主要是指针操作的错误。需要用gdb调试，在gcc编译时增加-g选项，使用gdb的backtrace命令查看段错误时的调用栈来定位错误。 定义新的宏来实现对空闲块的指针操作，以及显示空闲链表的头指针 // 显式空闲链表
// Given a free block pointer bp, compute the address of pred and succeed pointer in this free block
#define PRED(bp) (bp)
#define SUCC(bp) ((char *)bp + WSIZE)
// Given a free block pointer bp, compute the address of pred-free and succeed-free blocks of bp
#define PRED_FBLKP(bp) ((char *)GET(bp))
#define SUCC_FBLKP(bp) ((char *)GET(SUCC(bp))) // 需要转换成char类型指针，保证增加的地址是以字节为单位的
// 显式空闲链表
static char *free_list_head = 0; 两个对链表进行头插法和删除指定空闲块的函数 // 头插法
static void insert_head(void *new_free_bp) { PUT(SUCC(new_free_bp), free_list_head); PUT(PRED(new_free_bp), 0); if (free_list_head != 0) { PUT(PRED(free_list_head), new_free_bp); } free_list_head = new_free_bp;
} // 从空闲链表上删除一个空闲块
static void remove_block(void *bp) { if (PRED_FBLKP(bp) == 0 && SUCC_FBLKP(bp) == 0) { // 单个头节点 free_list_head = 0; } else if (PRED_FBLKP(bp) == 0) { // bp是头节点 free_list_head = SUCC_FBLKP(bp); PUT(PRED(SUCC_FBLKP(bp)), 0); PUT(PRED(bp), 0); PUT(SUCC(bp), 0); } else if (SUCC_FBLKP(bp) == 0) { PUT(SUCC(PRED_FBLKP(bp)), 0); PUT(PRED(bp), 0); PUT(SUCC(bp), 0); } else { PUT(SUCC(PRED_FBLKP(bp)), SUCC_FBLKP(bp)); PUT(PRED(SUCC_FBLKP(bp)), PRED_FBLKP(bp)); PUT(PRED(bp), 0); PUT(SUCC(bp), 0); }
} 更新后的函数（其他函数和书本上的一样） int mm_init(void)
{ if ((heap_listp = mem_sbrk(4 * WSIZE)) == (void *)-1) return -1; PUT(heap_listp, 0); // alignment padding PUT(heap_listp + (1*WSIZE), PACK(DSIZE, 1)); // prologue header PUT(heap_listp + (2*WSIZE), PACK(DSIZE, 1)); // prologue footer PUT(heap_listp + (3*WSIZE), PACK(0, 1)); // Epilogue header heap_listp += (2 * WSIZE); if ((free_list_head = extend_heap(CHUNKSIZE / WSIZE)) == NULL) { return -1; } PUT(PRED(free_list_head), 0); PUT(SUCC(free_list_head), 0); return 0;
} void *mm_malloc(size_t size)
{ Dprintf("Mallocing: %lu bytes.\n", size); size_t asize; // Adjusted block size size_t extendsize; // Amount to extend heap if no fit char *bp; if (size <= 0) { return NULL; } if (size <= DSIZE) { asize = 2 * DSIZE; } else { // 有效载荷大小 首尾 asize = DSIZE * ((size + DSIZE + DSIZE-1) / DSIZE); // 双字对齐 } if ((bp = find_fit(asize)) != NULL) { place(bp, asize);
// mm_check(1); return bp; } Dprint("Not enough space, extend Heap.\n"); extendsize = MAX(asize, CHUNKSIZE); if ((bp = extend_heap(extendsize/WSIZE)) == NULL) { return NULL; } remove_block(bp); place(bp, asize); if (DEBUG) { mm_check(1); } return bp;
} static void *find_fit(size_t asize) { void *bp; for (bp = free_list_head; bp != 0; bp = SUCC_FBLKP(bp)) { // debug
// print_block(bp); if (GET_SIZE(HDRP(bp)) >= asize) {
// printf("Find free block, remove it from list.\n"); remove_block(bp); return bp; } } return NULL;
} static void place(void *bp, size_t asize) { size_t blk_size = GET_SIZE(HDRP(bp)); size_t remain_size = blk_size - asize; size_t should_split = (remain_size >= 3 * DSIZE); // 剩余部分大于或等于最小块(4+4+4+4+1 => 24 bytes)的大小时进行分割 if (should_split) { // Important! remove the free block first, called before place() PUT(HDRP(bp), PACK(asize, 1)); PUT(FTRP(bp), PACK(asize, 1)); PUT(HDRP(NEXT_BLKP(bp)), PACK(remain_size, 0)); PUT(FTRP(NEXT_BLKP(bp)), PACK(remain_size, 0)); // insert the new free block to head void* new_free_block = NEXT_BLKP(bp); PUT(PRED(new_free_block), 0); PUT(SUCC(new_free_block), 0); insert_head(new_free_block); } else { PUT(HDRP(bp), PACK(blk_size, 1)); PUT(FTRP(bp), PACK(blk_size, 1)); }
}
static void *coalesce(void *bp) { size_t prev_alloc = GET_ALLOC(FTRP(PREV_BLKP(bp))); size_t next_alloc = GET_ALLOC(HDRP(NEXT_BLKP(bp))); size_t size = GET_SIZE(HDRP(bp)); if (prev_alloc && next_alloc) { Dprint("single block coalesce.\n"); insert_head(bp); return bp; } else if(prev_alloc && !next_alloc) { Dprint("Next block is free\n"); remove_block(NEXT_BLKP(bp)); size += GET_SIZE(HDRP(NEXT_BLKP(bp))); PUT(HDRP(bp), PACK(size, 0)); PUT(FTRP(bp), PACK(size, 0)); insert_head(bp); } else if (!prev_alloc && next_alloc) { Dprint("Prev block is free\n"); remove_block(PREV_BLKP(bp)); size += GET_SIZE(HDRP(PREV_BLKP(bp))); PUT(FTRP(bp), PACK(size, 0)); PUT(HDRP(PREV_BLKP(bp)), PACK(size, 0)); bp = PREV_BLKP(bp); insert_head(bp); } else { remove_block(PREV_BLKP(bp)); remove_block(NEXT_BLKP(bp)); size += GET_SIZE(HDRP(PREV_BLKP(bp))) + GET_SIZE(HDRP(NEXT_BLKP(bp))); PUT(HDRP(PREV_BLKP(bp)), PACK(size, 0)); PUT(FTRP(NEXT_BLKP(bp)), PACK(size, 0)); bp = PREV_BLKP(bp); insert_head(bp); } Dprint("\nAfter coalease.\n"); if (DEBUG) { mm_check(1); } return bp;
} 测试性能，可以看到对比之前的隐式链表有很大的性能提升 $ ./mdriver -V -t traces
